mapper.select:
select id, yplbname, yplbnumber, yplborder, yplbstatus from ylsys_ypleibie
1=1
and yplbname like CONCAT(CONCAT('%', #{yplbname}), '%')
and yplbnumber like CONCAT(CONCAT('%', #{yplbnumber}), '%')
and yplbstatus = #{yplbstatus}
order by ${sortname} ${sortorder}
LIMIT ${offset}, ${pagesize}

select count(0) from ylsys_ypleibie
1=1
and yplbname like CONCAT(CONCAT('%', #{yplbname}), '%')
and yplbnumber like CONCAT(CONCAT('%', #{yplbnumber}), '%')
and yplbstatus = #{yplbstatus}
order by ${sortname} ${sortorder}

mapper.update:
update ylsys_ypleibie


		yplbname = #{yplbname},


		yplbnumber = #{yplbnumber},


		yplborder = #{yplborder},


		yplbstatus = #{yplbstatus}



where id = #{id}
